import * as React from 'react';
import { useState, useEffect } from 'react';
import {
  Button,
  Pressable,
  StyleSheet,
  Text,
  TextInput,
  View,
  Switch,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import {
  initSession,
  PeerState,
  RNPeer,
} from 'react-native-multipeer-connectivity';
import RNFS from 'react-native-fs';
import { produce } from 'immer';
import DocumentPicker from 'react-native-document-picker';

export default function App() {
  // --- state hooks ---
  const [displayName, setDisplayName] = useState('');
  const [persistentID, setPersistentID] = useState('');
  const [peerID, setPeerID] = useState('');
  const [broadcastMessage, setBroadcastMessage] = useState('');
  const [isBrowsing, setIsBrowsing] = useState(false);
  const [isAdvertising, setIsAdvertising] = useState(false);
  const [peers, setPeers] = useState<
    Record<
      string,
      { state: PeerState; peer: RNPeer; discoveryInfo?: Record<string, string> }
    >
  >({});
  const [receivedMessages, setReceivedMessages] = useState<
    Record<string, string[]>
  >({});
  const [session, setSession] = useState<null | ReturnType<typeof initSession>>(
    null
  );

  // --- helper functions ---
  const generateID = () =>
    Date.now().toString(36) + Math.random().toString(36).substring(2, 10);

  function chunkBase64(base64String: string, chunkSize = 4000) {
    const chunks: string[] = [];
    for (let i = 0; i < base64String.length; i += chunkSize) {
      chunks.push(base64String.slice(i, i + chunkSize));
    }
    return chunks;
  }


async function stitchBase64(chunks: string[]) {
  if (!chunks || chunks.length === 0) return;

  const base64String = chunks.join("");

  try {
    // Save to appâ€™s document directory
    const destPath = RNFS.DocumentDirectoryPath;
    const fileName = "reconstructed_file.jpeg";
    const filePath = `${destPath}/${fileName}`;

    await RNFS.writeFile(filePath, base64String, "base64");

    console.log(`File reconstructed and saved at: ${filePath}`);
  } catch (err) {
    console.error("Failed to save file:", err);
  }
}

  const pickAndChunkFile = async () => {
    try {
      const res = await DocumentPicker.pickSingle({
        type: [DocumentPicker.types.allFiles],
      });

      let filePath = res.uri;
      if (filePath.startsWith("file://")) filePath = filePath.slice(7);

      const base64 = await RNFS.readFile(filePath, "base64");

      const chunks = chunkBase64(base64, 4000);

      // TODO : for each chunk sent it over

      console.log(`File split into ${chunks.length} chunks`);
    } catch (err) {
      if (!DocumentPicker.isCancel(err)) console.error(err);
    }
  };

  // Button press handler
  const testReceiveFiles = async () => {
    console.log("receiving hello world")
    const dummyChunks = [
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      ""
];
    await stitchBase64(dummyChunks);
  };

  // --- load persistent ID ---
  useEffect(() => {
    (async () => {
      let storedID = await AsyncStorage.getItem('persistentPeerID');
      if (!storedID) {
        storedID = generateID();
        await AsyncStorage.setItem('persistentPeerID', storedID);
      }
      setPersistentID(storedID);
    })();
  }, []);

  // --- session event handlers ---
  useEffect(() => {
    if (!session) return;

    const r1 = session.onStartAdvertisingError(() => setIsAdvertising(false));
    const r2 = session.onStartBrowsingError(() => setIsBrowsing(false));

    const r3 = session.onFoundPeer((ev) => {
      setPeers(
        produce((draft) => {
          if (!draft[ev.peer.id]) {
            draft[ev.peer.id] = {
              peer: ev.peer,
              state: PeerState.notConnected,
              discoveryInfo: ev.discoveryInfo,
            };
            session?.invite(ev.peer.id);
          }
        })
      );
    });

    const r4 = session.onLostPeer((ev) => {
      setPeers(
        produce((draft) => {
          delete draft[ev.peer.id];
        })
      );
    });

    const r5 = session.onPeerStateChanged((ev) => {
      setPeers(
        produce((draft) => {
          if (draft[ev.peer.id]) draft[ev.peer.id].state = ev.state;
          else draft[ev.peer.id] = { peer: ev.peer, state: ev.state };
        })
      );

      if (ev.state !== PeerState.connected) {
        const retryInvite = (attempts = 0) => {
          if (
            session &&
            peers[ev.peer.id]?.state !== PeerState.connected &&
            attempts < 10
          ) {
            session.invite(ev.peer.id);
            setTimeout(() => retryInvite(attempts + 1), 1000);
          }
        };
        retryInvite();
      }
    });

    const r6 = session.onReceivedPeerInvitation((ev) => ev.handler(true));

    const r7 = session.onReceivedText((ev) => {
      const msg = ev.text;

      setReceivedMessages(
        produce((draft) => {
          (draft[ev.peer.id] ||= []).push(msg);
        })
      );

      // relay to other connected peers
      Object.keys(peers).forEach((id) => {
        if (id !== ev.peer.id && peers[id].state === PeerState.connected) {
          session?.sendText(id, msg);
        }
      });
    });

    return () => {
      session.stopAdvertizing();
      session.stopBrowsing();
      r1.remove();
      r2.remove();
      r3.remove();
      r4.remove();
      r5.remove();
      r6.remove();
      r7.remove();
    };
  }, [session]);

  if (!displayName || !persistentID) {
    return (
      <View style={styles.container}>
        <Text style={{ fontSize: 20, marginBottom: 5 }}>
          Enter your display name:
        </Text>
        <TextInput
          style={{ fontSize: 30, borderWidth: 1, padding: 10, width: 300 }}
          placeholder="display name"
          onSubmitEditing={async (ev) => {
            const name = ev.nativeEvent.text;
            setDisplayName(name);

            const s = initSession({
              displayName: name,
              serviceType: "demo",
              discoveryInfo: {
                myPersistentID: persistentID,
                myName: name,
                joinAt: Date.now().toString(),
              },
            });

            setSession(s);
            setPeerID(s.peerID);
          }}
        />
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 16, marginBottom: 10 }}>
        my persistent ID: {persistentID}
      </Text>

      <View style={{ marginVertical: 20 }}>
        <View style={styles.toggleRow}>
          <Text>Set Available</Text>
          <Switch
            value={isBrowsing}
            onValueChange={(v) => {
              setIsBrowsing(v);
              v ? session?.browse() : session?.stopBrowsing();
              setIsAdvertising(v);
              v ? session?.advertize() : session?.stopAdvertizing();
            }}
          />
        </View>
      </View>

      <Button
        title="Disconnect"
        onPress={() => {
          session?.disconnect();
          setPeers({});
        }}
      />

      {/* Broadcast messages */}
      <View style={{ marginTop: 20, width: "90%" }}>
        <Text>Broadcast message to all peers:</Text>
        <TextInput
          style={{ borderWidth: 1, padding: 5, marginTop: 5 }}
          placeholder="Enter broadcast message"
          value={broadcastMessage}
          onChangeText={setBroadcastMessage}
          onSubmitEditing={() => {
            const msg = broadcastMessage.trim();
            if (!msg) return;

            const formattedMsg = "[ðŸ“£] " + msg;

            Object.keys(peers).forEach((id) => {
              if (peers[id].state === PeerState.connected) {
                session?.sendText(id, formattedMsg);
                setReceivedMessages(
                  produce((draft) => {
                    (draft[id] ||= []).push(formattedMsg);
                  })
                );
              }
            });

            setBroadcastMessage("");
          }}
        />
      </View>

      {/* Pick and chunk file */}
      <View style={{ marginTop: 20 }}>
        <Button title="Pick and Chunk File" onPress={pickAndChunkFile} />
      </View>

      <View style={{ marginTop: 20 }}>
        <Button title="Test Receive Files" onPress={testReceiveFiles} />
      </View>

      {/* Found peers */}
      <View style={{ marginTop: 30, width: "90%" }}>
        <Text>Found peers:</Text>
        {Object.entries(peers).map(([id, info]) => (
          <View key={id} style={styles.peerBox}>
            <Pressable
              onPress={() => {
                if (info.state !== PeerState.connected) session?.invite(id);
              }}
            >
              <Text>
                {id} - {info.state}
              </Text>
              <Text>displayName: {info.peer.displayName}</Text>
              <Text>persistentID: {info.discoveryInfo?.myPersistentID}</Text>
            </Pressable>

            {info.state === PeerState.connected && (
              <>
                <TextInput
                  style={{ borderWidth: 1, marginTop: 5 }}
                  placeholder="send a message"
                  onSubmitEditing={(ev) => {
                    if (ev.nativeEvent.text.trim())
                      session?.sendText(id, ev.nativeEvent.text);
                  }}
                />

                <View style={{ marginTop: 10 }}>
                  <Text>Message History:</Text>
                  {receivedMessages[id] &&
                    receivedMessages[id].map((msg, idx) => (
                      <Text key={idx}>{msg}</Text>
                    ))}
                </View>
              </>
            )}
          </View>
        ))}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "flex-start",
    paddingTop: 80,
    backgroundColor: "white",
  },
  toggleRow: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    width: 200,
    marginVertical: 10,
  },
  peerBox: { borderWidth: 1, padding: 8, marginVertical: 10 },
});
